import { reactive, readonly, computed } from "vue";
import connectMetamask from "./connectMetamask";

const defaultState = {
  address: "",
  signer: null,
  status: false,
};

const state = reactive(defaultState);

const getters = {
  getStatus: () => {
    return computed(() => state.status);
  },
};

const actions = {
  connectMetamask
};

export default () => ({
  state: state,
  ...getters,
  ...actions,
});
